import React, { useEffect, useState,useRef} from 'react';
import {Button, Container, Image, Row,Table} from "react-bootstrap";
import {useParams} from 'react-router-dom';
import {fetchOneDevice} from "../http/deviceAPI";
import ReactDOM from "react-dom";
import { useReactToPrint } from "react-to-print";

const Report = React.forwardRef((props, ref) => {
    const {id} = useParams();
    const [deviceCurr, setDeviceCurr] = useState({});

    const [selectBrand, setSelectBrand] = useState({});
    const [selectType, setSelectType] = useState({});
    const [name, setName] = useState("");
    const [price, setPrice] = useState(0);
    const [img, setImg] = useState("");
    const [info, setInfo] = useState({});
   
    const componentRef = useRef();
    const handlePrint = useReactToPrint({
      content: () => componentRef.current});
    


   

    useEffect(() => {
        fetchOneDevice(id).then(data => {
            setDeviceCurr(data);
            setSelectBrand(data.brand);
            setSelectType(data.type);
            setName(data.name);
            setPrice(data.price);
            setInfo(data.info)
        });
    }, [id]);

    return (
        
        <Container className="mt-3" onLoad="window.print()">
            <Table className="table">
        <thead>
            <tr><th>Изображение</th>
                <th>Название</th>
                <th>Форма обучения</th>
                <th>Район</th>
                <th>Стоимость</th>
            </tr>
        </thead>
        <tbody>
            <tr>
                <td>
                <Image width={150} src={process.env.REACT_APP_API_URL + deviceCurr.img}/>
                </td>
                <td>{name}</td>
                <td>{selectBrand.name}</td>
                <td>{selectType.name}</td>
                <td>{price}</td>
                                </tr>
        </tbody>
    </Table>
    <div className="bg-gray-200 p-6">
      <button
        type="button"
        className="bg-gray-500 border border-gray-500 p-2 mb-4"
        onClick={handlePrint}
      >
        {" "}
        Print Resume{" "}
      </button>
      <Report ref={componentRef} />
    </div>
</Container>

    );
});

  
export default Report;
