import React, {useContext} from 'react';
import {observer} from "mobx-react-lite";
import {Context} from "../index";
import {ListGroup} from "react-bootstrap";

const RatingBar = observer(() => {
    const {device} = useContext(Context);

    const getAllDevices = () => {
        device.setSelectedType("all");
        device.setSelectedBrand("all");
        device.setSelectedRating("all");
    }

    return (
        <ListGroup>
            <ListGroup.Item
                style={{cursor: "pointer"}}
                active={"all" === device.selectedRating}
                onClick={getAllDevices}
            >
                Все
            </ListGroup.Item>
            {device.types.map(rating =>
                <ListGroup.Item
                    style={{cursor: "pointer"}}
                    active={rating.id === device.selectedRating.id}
                    key={rating.id}
                    onClick={() => device.setSelectedRating(rating)}
                >
                    {rating.rate}
                </ListGroup.Item>
            )}
        </ListGroup>
    );
});

export default RatingBar;
