import React, {useContext, useEffect, useState} from 'react';
import {Button, Col, Container, Dropdown, Form, Image, Modal, Row} from "react-bootstrap";
import {useParams} from 'react-router-dom';
import {fetchOneDevice} from "../http/deviceAPI";
import {Context} from "../index";


const Report = () => {
    const {device} = useContext(Context);
    const {id} = useParams();
    const [deviceCurr, setDeviceCurr] = useState({});

    const [selectBrand, setSelectBrand] = useState({});
    const [selectType, setSelectType] = useState({});
    const [name, setName] = useState("");
    const [price, setPrice] = useState(0);
    const [img, setImg] = useState("");
    const [imgFile, setImgFile] = useState(null);
    const [info, setInfo] = useState({});

    
   

   

    useEffect(() => {
        fetchOneDevice(id).then(data => {
            setDeviceCurr(data);
            setSelectBrand(data.brand);
            setSelectType(data.type);
            setName(data.name);
            setPrice(data.price);
            setInfo(data.info)
        });
    }, [id]);

    return (
        
        <Container className="mt-3">
           
            <Row>
            <table className="table">
        <thead>
            <tr>
                <th>First Name</th>
                <th>Last Name</th>
                <th>E-mail</th>
                <th>Phone</th>
                <th>AAA</th>
            </tr>
        </thead>
        <tbody>
            <tr>
                <td>{name}</td>
                <td>{price}</td>
                <td>{selectBrand.name}</td>
                <td>{selectType.name}</td>
                <td>{info.title}: {info.description}</td>
                                </tr>
        </tbody>
    </table>
               
            </Row>
        </Container>
    );
};

export default Report;
