import React, { useEffect, useState} from 'react';
import {Button, Container, Image, Row} from "react-bootstrap";
import {useParams} from 'react-router-dom';
import {fetchOneDevice} from "../http/deviceAPI";
import ReactToPrint from 'react-to-print';

export class ComponentToPrint extends React.PureComponent {
    render() {
      return (
        <table>
          <thead>
            <th>column 1</th>
            <th>column 2</th>
            <th>column 3</th>
          </thead>
          <tbody>
            <tr>
              <td>data 1</td>
              <td>data 2</td>
              <td>data 3</td>
            </tr>
          </tbody>
        </table>
      );
    }
  }


const Report = () => {
    const {id} = useParams();
    const [deviceCurr, setDeviceCurr] = useState({});

    const [selectBrand, setSelectBrand] = useState({});
    const [selectType, setSelectType] = useState({});
    const [name, setName] = useState("");
    const [price, setPrice] = useState(0);
    const [img, setImg] = useState("");
    const [info, setInfo] = useState({});

    
   

   

    useEffect(() => {
        fetchOneDevice(id).then(data => {
            setDeviceCurr(data);
            setSelectBrand(data.brand);
            setSelectType(data.type);
            setName(data.name);
            setPrice(data.price);
            setInfo(data.info)
        });
    }, [id]);

    return (
        
        <Container className="mt-3" onLoad="window.print()">
            <table className="table">
        <thead>
            <tr><th>Изображение</th>
                <th>Название</th>
                <th>Форма обучения</th>
                <th>Район</th>
                <th>Стоимость</th>
            </tr>
        </thead>
        <tbody>
            <tr>
                <td>
                <Image width={150} src={process.env.REACT_APP_API_URL + deviceCurr.img}/>
                </td>
                <td>{name}</td>
                <td>{selectBrand.name}</td>
                <td>{selectType.name}</td>
                <td>{price}</td>
                                </tr>
        </tbody>
    </table>
</Container>
    );
};

export default Report;
