import React, {useContext, useEffect, useState} from 'react';
import {Button, Col, Container, Dropdown, Form, Image, Modal, Row} from "react-bootstrap";
import {useParams, useHistory} from 'react-router-dom';
import {fetchDeleteDevice, fetchOneDevice, updateDevices} from "../http/deviceAPI";
import {Context} from "../index";
import {ADMIN_ROUTE} from "../utils/consts";


const DevicePageEdit = () => {
    const {device} = useContext(Context);
    const history = useHistory();
    const {id} = useParams();
 
    const [selectBrand, setSelectBrand] = useState({});
    const [selectType, setSelectType] = useState({});
    const [name, setName] = useState("");
    const [price, setPrice] = useState(0);
   
   

   

    useEffect(() => {
        fetchOneDevice(id).then(data => {
            setDeviceCurr(data);
            setSelectBrand(data.brand);
            setSelectType(data.type);
            setName(data.name);
            setPrice(data.price);
            setInfo(data.info)
        });
    }, [id]);

    return (
        
        <Container className="mt-3">
           
            <Row>
            <table className="table">
        <thead>
            <tr>
                <th>First Name</th>
                <th>Last Name</th>
                <th>E-mail</th>
                <th>Phone</th>
                <th>AAA</th>
            </tr>
        </thead>
        <tbody>
            <tr>
                <td>{name}</td>
                <td>{price}</td>
                <td>{selectBrand.name}</td>
                <td>{selectType.name}</td>
                                </tr>
        </tbody>
    </table>
               
            </Row>
        </Container>
    );
};

export default DevicePageEdit;
